import React, { useState } from "react";
import { Button } from "@material-ui/core";

function SecondStep({ onGoBack }) {
  const [revenueState, setRevenueState] = useState(0);
  const [revenueNeighbor1, setRevenueNeighbor1] = useState(0);
  const [revenueNeighbor2, setRevenueNeighbor2] = useState(0);

  const scoreMatrix = {
    "Do Nothing": {
      "Farm, Farm": [10, 10, 10],
      "Farm, Pasture": [9, 21, 5],
      "Pasture, Farm": [21, 9, 5],
      "Pasture, Pasture": [5, 5, 0]
    },
    "Survey": {
      "Farm, Farm": [9, 9, 5],
      "Farm, Pasture": [10, 16, 5],
      "Pasture, Farm": [16, 10, 5],
      "Pasture, Pasture": [10, 10, 10]
    },
    "Extract Resources": {
      "Farm, Farm": [5, 5, 15],
      "Farm, Pasture": [5, 10, 10],
      "Pasture, Farm": [10, 5, 10],
      "Pasture, Pasture": [0, 0, 0]
    },
    "Impose Fine": {
      "Farm, Farm": [9, 9, 5],
      "Farm, Pasture": [10, 20, 10],
      "Pasture, Farm": [20, 10, 10],
      "Pasture, Pasture": [0, 0, 20]
    }
  };

  const getNeighborAction = () => {
    return Math.random() < 0.5 ? "Farm" : "Pasture";
  };

  const handleStateAction = (action) => {
    const neighbor1Action = getNeighborAction();
    const neighbor2Action = getNeighborAction();
    const [newRevenueNeighbor1, newRevenueNeighbor2, newRevenueState] = scoreMatrix[action][`${neighbor1Action}, ${neighbor2Action}`];

    setRevenueState(revenueState + newRevenueState);
    setRevenueNeighbor1(revenueNeighbor1 + newRevenueNeighbor1);
    setRevenueNeighbor2(revenueNeighbor2 + newRevenueNeighbor2);
  };

  return (
    <div style={{ display: 'flex', flexDirection: 'column', alignItems: 'center', justifyContent: 'center', height: '100vh' }}>
      <h1>Welcome to the second step!</h1>
      <div style={{ display: 'flex', justifyContent: 'center', gap: '20px' }}>
        <p>State Revenue: {revenueState}</p>
        <p>Neighbor 1 Revenue: {revenueNeighbor1}</p>
        <p>Neighbor 2 Revenue: {revenueNeighbor2}</p>
      </div>
      <Button variant="contained" color="default" onClick={() => handleStateAction('Do Nothing')}>Do Nothing</Button>
      <Button variant="contained" color="default" onClick={() => handleStateAction('Survey')}>Survey</Button>
      <Button variant="contained" color="default" onClick={() => handleStateAction('Extract Resources')}>Extract Resources</Button>
      <Button variant="contained" color="default" onClick={() => handleStateAction('Impose Fine')}>Impose Fine</Button>
      <Button variant="contained" color="default" onClick={onGoBack} style={{ marginTop: '20px' }}>Go Back to First Step</Button>
    </div>
  );
}

export default SecondStep;
