import React, { useState } from "react";
import { Button, Slider } from "@material-ui/core";
import './SecondStep.css';

const months = ['January', 'February', 'March', 'April', 'May', 'June', 'July', 'August', 'September', 'October', 'November', 'December'];

function SecondStep({ onGoBack }) {
  const [revenueState, setRevenueState] = useState(0);
  const [revenueFirstFarmer, setRevenueFirstFarmer] = useState(0);
  const [revenueSecondFarmer, setRevenueSecondFarmer] = useState(0);
  const [steps, setSteps] = useState(0);
  const [taxRate, setTaxRate] = useState(0);
  const [lastAction, setLastAction] = useState(null);

  const scoreMatrix = {
    "Do Nothing": [10, 10, 10],
    "Survey Pasture": [9, 9, 5],
    "Extract Resources": [5, 5, 15],
    "Collect Tax/Impose Fine": [(9 - taxRate / 100), (9 - taxRate / 100), (5 + 2 * taxRate / 100)]
  };

  const reset = () => {
    setRevenueState(0);
    setRevenueFirstFarmer(0);
    setRevenueSecondFarmer(0);
    setSteps(0);
    setLastAction(null);
  };

  const handleStateAction = (action) => {
    if (steps >= 12) return;

    const [newRevenueFirstFarmer, newRevenueSecondFarmer, newRevenueState] = scoreMatrix[action];

    setRevenueState(revenueState + newRevenueState);
    setRevenueFirstFarmer(revenueFirstFarmer + newRevenueFirstFarmer);
    setRevenueSecondFarmer(revenueSecondFarmer + newRevenueSecondFarmer);
    setSteps(steps + 1);
    setLastAction(action);
  };

  const getCurrentMonth = () => {
    if (steps < 12) return "It's " + months[steps];
    return "It's time to reflect";
  };

  return (
    <div style={{ display: 'flex', flexDirection: 'column', alignItems: 'center', justifyContent: 'center', height: '100vh' }}>
      <img src="https://www.foodandwine.com/thmb/zG2tLe411fyVEy_gIR3JTfCQEuc=/1500x0/filters:no_upscale():max_bytes(150000):strip_icc()/best-farms-in-america-soul-fire-farm-FT-BLOG0720-2-4835d975143c44f1bff76d1218a730cc.jpg" alt="Farm" className="farmImage" />
      <h1>Welcome to Seeing like a State!</h1>
      <p>State Revenue: $ {revenueState} :: First Farmer Revenue: $ {revenueFirstFarmer} :: Second Farmer Revenue: $ {revenueSecondFarmer}</p>
      <h2>{getCurrentMonth()}</h2>
      {lastAction && <p>Last month, the state chose to {lastAction.toLowerCase()}.</p>}
      <Button variant="contained" color="default" onClick={() => handleStateAction('Do Nothing')}>Do Nothing</Button>
      <Button variant="contained" color="default" onClick={() => handleStateAction('Survey Pasture')}>Survey Pasture</Button>
      <Button variant="contained" color="default" onClick={() => handleStateAction('Extract Resources')}>Extract Resources</Button>
      <div>
        <Button variant="contained" color="default" onClick={() => handleStateAction('Collect Tax/Impose Fine')}>Collect Tax/Impose Fine</Button>
        <Slider value={taxRate} onChange={(event, newValue) => setTaxRate(newValue)} aria-labelledby="discrete-slider" valueLabelDisplay="auto" step={5} marks min={0} max={30} getAriaValueText={valuetext}/>
      </div>
      <Button variant="contained" color="default" onClick={reset} style={{ marginTop: '20px' }}>Reset Seeing Like A State</Button>
      <Button variant="contained" color="default" onClick={onGoBack} style={{ marginTop: '20px' }}>Go Back to Being a Farmer</Button>
    </div>
  );
}

export default SecondStep;
