import React, { useState } from "react";
import { Button } from "@material-ui/core";
import './SecondStep.css';

function SecondStep({ onGoBack }) {
  const [revenueState, setRevenueState] = useState(0);
  const [revenueFirstFarmer, setRevenueFirstFarmer] = useState(0);
  const [revenueSecondFarmer, setRevenueSecondFarmer] = useState(0);
  const [steps, setSteps] = useState(0);

  const months = ['January', 'February', 'March', 'April', 'May', 'June', 'July', 'August', 'September', 'October', 'November', 'December', "It's time to reflect"];
  const hayekConsiderations = [
    "Do you think the farmers can manage their resources better than the state?",
    "Is there a way to ensure you both get the maximum happiness?",
    "Would it make a difference if the farmers knew about your choice beforehand?",
    "What's the best strategy if you could communicate with the farmers?",
    "Can a heavy-handed approach lead to higher revenues for the state?",
    "How would the dynamics change if there were more farmers?",
    "Would you change your strategy if you knew the farmers were very competitive?",
    "What if the farmers are kind and always comply with the state's regulations?",
    "Is it possible to always win in this game?",
    "Can you predict the outcome of the game based on the first few rounds?",
    "What if you had more resources at your disposal? Would it change your strategy?",
    "Reflect on your actions and their consequences. Did your choices lead to a prosperous state?"
  ];

  const scoreMatrix = {
    "Do Nothing": {
      "Farm, Farm": [10, 10, 10],
      "Farm, Pasture": [9, 21, 5],
      "Pasture, Farm": [21, 9, 5],
      "Pasture, Pasture": [5, 5, 0]
    },
    "Survey Pasture": {
      "Farm, Farm": [9, 9, 5],
      "Farm, Pasture": [10, 16, 5],
      "Pasture, Farm": [16, 10, 5],
      "Pasture, Pasture": [10, 10, 10]
    },
    "Extract Resources": {
      "Farm, Farm": [5, 5, 15],
      "Farm, Pasture": [5, 10, 10],
      "Pasture, Farm": [10, 5, 10],
      "Pasture, Pasture": [0, 0, 0]
    },
    "Collect Tax/Impose Fine": {
      "Farm, Farm": [9, 9, 5],
      "Farm, Pasture": [10, 20, 10],
      "Pasture, Farm": [20, 10, 10],
      "Pasture, Pasture": [0, 0, 20]
    }
  };

  const getNeighborAction = () => {
    return Math.random() < 0.5 ? "Farm" : "Pasture";
  };

  const reset = () => {
    setRevenueState(0);
    setRevenueFirstFarmer(0);
    setRevenueSecondFarmer(0);
    setSteps(0);
  };

  const handleStateAction = (action) => {
    if (steps >= 12) return;

    const neighbor1Action = getNeighborAction();
    const neighbor2Action = getNeighborAction();
    const [newRevenueFirstFarmer, newRevenueSecondFarmer, newRevenueState] = scoreMatrix[action][`${neighbor1Action}, ${neighbor2Action}`];

    setRevenueState(revenueState + newRevenueState);
    setRevenueFirstFarmer(revenueFirstFarmer + newRevenueFirstFarmer);
    setRevenueSecondFarmer(revenueSecondFarmer + newRevenueSecondFarmer);
    setSteps(steps + 1);
  };

  const getCurrentMonth = () => {
    return months[steps];
  };

  const getCurrentMonthConsideration = () => {
    return hayekConsiderations[steps];
  };

  return (
    <div className="SecondStep">
      <img src="https://www.foodandwine.com/thmb/zG2tLe411fyVEy_gIR3JTfCQEuc=/1500x0/filters:no_upscale():max_bytes(150000):strip_icc()/best-farms-in-america-soul-fire-farm-FT-BLOG0720-2-4835d975143c44f1bff76d1218a730cc.jpg" alt="Farm" className="farmImage" />
      <h1>Welcome to Seeing Like a State!</h1>
      <p className="storyline">
        Welcome, Governor! You're no longer a humble shepherd but the leader of the entire region. Both the first and second farmer live and work under your jurisdiction. You have the power to implement policies and make decisions on behalf of the region. Will you choose to do nothing and let the farmers manage their affairs? Will you survey the pasture to monitor the farmers' actions? Will you extract resources from the pasture for the state? Or will you collect tax and impose fines to regulate the farmers? Choose your actions wisely, for the prosperity of your state and the farmers depends on it.
      </p>
      <p className="consideration">It's {getCurrentMonth()}. {getCurrentMonthConsideration()}</p>
      <p>State Revenue: ${revenueState} :: First Farmer Revenue: ${revenueFirstFarmer} :: Second Farmer Revenue: ${revenueSecondFarmer}</p>
      <Button variant="contained" color="default" onClick={() => handleStateAction('Do Nothing')}>Do Nothing</Button>
      <Button variant="contained" color="default" onClick={() => handleStateAction('Survey Pasture')}>Survey Pasture</Button>
      <Button variant="contained" color="default" onClick={() => handleStateAction('Extract Resources')}>Extract Resources</Button>
      <Button variant="contained" color="default" onClick={() => handleStateAction('Collect Tax/Impose Fine')}>Collect Tax/Impose Fine</Button>
      <div className="actionButtons">
        <Button variant="contained" color="default" onClick={reset}>Reset Seeing Like a State</Button>
        <Button variant="contained" color="default" onClick={onGoBack}>Go Back to Being a Farmer</Button>
      </div>
    </div>
  );
}

export default SecondStep;
